import { useState, useEffect } from "react";

import { Link } from "react-router-dom";
//there seems to be a issue with the rendering of the header component.
//the header component is not rendering properly.
//the header component is not rendering properly.

export default function Headers() {
	const [username, setUsername] = useState(null);
	useEffect(() => {
    const storedUsername = localStorage.getItem('username');
    if (storedUsername) {
      setUsername(storedUsername);
    }
  }, []);
	const handleLogout = () => {
    setUsername(null);
    localStorage.removeItem('username');
  };
	const handleLogin = (loggedInUsername) => {
    setUsername(loggedInUsername);
    localStorage.setItem('username', loggedInUsername);
  };
	//useeffect is a hook that is used to perform side effects in a functional component.
	//useeffect is a hook that is used to perform side effects in a functional component.
	useEffect(() => {
		fetch("http://localhost:4000/profile", {
			credentials: "include",
		})
			.then((res) => {
				if (res.ok) {
					return res.json({ username: res.username});
				} else {
					throw new Error("You must be logged in");
				}
			})
			.then((res) => {
				setUsername(res.username);
			})
			.catch((err) => {
				setUsername(null);
			});
	}, []);

	function logout() {
		fetch("http://localhost:4000/logout", {
			credentials: "include",
			method: "POST",
		}).then(()=>{
			setUsername(null)
		})
	}

	return (
		<header>
			<Link to="/" className="logo">
				Blog
			</Link>
			<nav>
				{username ? (
					<>
						<Link to="/create">Create new Post</Link>
						<a href="https://google.com" onClick={logout}>
							Logout
						</a>
					</>
				) : (
					<>
						<Link to="/login">Login</Link>
						<Link to="/register">Register</Link>
					</>
				)}
			</nav>
		</header>
	);
				}
