import React from 'react';
import {formatISO9075} from 'date-fns';
function Post({title,summary,cover,content,createdAt})
{

  return (
    <div className="post">
      
        <img alt="image0" src="https://images.unsplash.com/photo-1471107340929-a87cd0f5b5f3?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1973&q=80"/>
        <div className="text">
        <h2>{title}</h2>
          <p className="info">
            <a className="author">Marcus aurelius</a>
            <time>
              {/* used for foramtting timing */}
              {formatISO9075(new Date(createdAt), 'MMM d, ')}
            </time>
          </p>
        <p className="summary">{summary}
        </p>
        </div>
  
      </div>
  )
}
export default Post;
